import "./App.css";
import { useState } from "react";
import Button from "./components/button/Button";
import Modal from "./components/modal/Modal";

function App() {
  const [modal, setModal] = useState(false);
  return (
    <div className="App">
      <h1>Click-Me for Modal.</h1>
      <Button
        name="Get Modalled"
        onClick={() => {
          setModal(true);
        }}
      />
      { modal && <Modal />}
    </div>
  );
}

export default App;
