import './App.css';
import Button from './components/button/Button';
import Modal from './components/modal/Modal';

function App() {
  const [modal, setmodal] = useState(false);
  return (
    <div className="App">
      <h1>Click-Me for Modal.</h1>
      <Button name="Get Modalled" onClick={()=>{modal(true)}}/>
      <Modal/>
    </div>
  );
}

export default App;
