import "./App.css";
import { useState } from "react";
import Modal from "./components/modal/Modal";

function App() {
  const [modal, setModal] = useState(false);
  return (
    <div className="App">
      <h1>Click-Me for Modal.</h1>

      <button
        onClick={() => {
          setModal(true);
        }}
      >
        open
      </button>
      {modal && <Modal />}
    </div>
  );
}

export default App;
